<?php
namespace Library\Ioc\Container;

class Library
{
    private static $mocks;

    /**
     * Mock an object.
     * @param string $from The class name to mock
     * @param object $to The object to return instead of the class
     */
    public static function mock($from, $to)
    {
        self::$mocks[$from] = $to;
    }

    /*
     * The following functions are generated by Compiler.php
     */
    public static function View()
    {
        if (!isset(self::$mocks['View'])) {
            return new \Library\View();
        } else {
            return self::$mocks['View'];
        }
    }
}